import { ref, computed } from 'vue';
import en from '@/locales/en.json';
import nl from '@/locales/nl.json';

const messages = { en, nl };
const currentLocale = ref('nl'); // Set your default language here

export const i18n = {
  locale: computed(() => currentLocale.value),
  t: (key) => {
    const localeMessages = messages[currentLocale.value]; // Access the correct locale messages
    return localeMessages[key] || key; // Return the translated value or the key if not found
  },
  changeLanguage(locale) {
    if (messages[locale]) {
      currentLocale.value = locale;
    }
  }
};
