import { createWebHistory, createRouter } from "vue-router";
import { i18n } from '../plugins/i18n'

const routes = [
  {
    path: "/:locale?",
    name: "index",
    component: () => import('../pages/index.vue'),
  },
  {
    path: "/:locale?/contact-one",
    name: "contact-one",
    component: () => import('../pages/contact-one.vue'),
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

router.beforeEach((to, from, next) => {
  // Determine the locale from the route parameters or default to 'nl'
  const locale = to.params.locale || 'nl';

  // Validate the locale
  if (!['en', 'nl'].includes(locale)) {
    return next({ path: '/nl' }); // Redirect to Nederlands if the locale is invalid
  }

  // Change the language using i18n
  i18n.changeLanguage(locale);

  // Redirect to the default route without locale if locale is 'nl'
  if (!to.params.locale && locale === 'nl') {
    return next({ path: '/' }); // Redirect to the root without locale
  }

  // Allow the navigation to proceed
  next();
});

export default router;
