import { createWebHistory, createRouter } from "vue-router";

const routes = [
  {
    path: "/",
    children: [
      {
        path: "",
        name: "index",
        component: () => import('../pages/index.vue'),
      },
      {
        path: "contact-one",
        name: "contact-one",
        component: () => import('../pages/contact-one.vue'),
      },
    ]
  },
  {
    path: "/en",
    children: [
      {
        path: "",
        name: "index-en",
        component: () => import('../pages/index.vue'),
      },
      {
        path: "contact-one",
        name: "contact-one-en",
        component: () => import('../pages/contact-one.vue'),
      },
    ]
  }
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

// Navigation guard to enforce Dutch as default
router.beforeEach((to, from, next) => {
  // Default to Dutch version for root path
  if (to.path === '/') {
    return next();
  }
  next();
});

export default router;
