import "core-js/modules/es.array.push.js";
export default {
  name: 'LanguageSwitcher',
  computed: {
    currentLocale() {
      return this.$i18n.locale.value;
    }
  },
  methods: {
    changeLanguage(event) {
      const selectedLocale = event.target.value;
      this.$i18n.changeLanguage(selectedLocale);

      // If 'nl' is selected, go to the base route '/' without a locale
      if (selectedLocale === 'nl') {
        this.$router.push({
          path: '/'
        });
      } else {
        // If 'en' is selected, add /en to the route
        this.$router.push({
          name: this.$route.name,
          params: {
            locale: selectedLocale
          }
        });
      }
    }
  }
};
