import { createWebHistory, createRouter } from "vue-router";
import { i18n } from '../plugins/i18n'; // Import i18n to manage languages

const routes = [
  {
    path: "/:locale?",
    name: "index",
    component: () => import('../pages/index.vue'),
  },
  {
    path: "/:locale?/contact-one",
    name: "contact-one",
    component: () => import('../pages/contact-one.vue'),
  },
  // Add other routes here with the locale parameter as needed
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

// Global navigation guard to handle locale and change language
router.beforeEach((to, from, next) => {
  const locale = to.params.locale || 'nl'; // Default to 'nl' if no locale is provided

  // Check if the locale is valid
  if (!['en', 'nl'].includes(locale)) {
    return next({ name: 'index', params: { locale: 'nl' }}); // Redirect to Dutch if invalid
  }
  
  // Change the language in i18n
  i18n.changeLanguage(locale);  
  
  // Proceed to the next route
  next();
});

export default router;
