import { createWebHistory, createRouter } from "vue-router";
import { i18n } from '../plugins/i18n'

const routes = [
  {
    path: "/:locale?",
    name: "index",
    component: () => import('../pages/index.vue'),
  },
  {
    path: "/:locale?/contact-one",
    name: "contact-one",
    component: () => import('../pages/contact-one.vue'),
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

router.beforeEach((to, from, next) => {
  const locale = localStorage.getItem('locale') || 'nl'; // Default to Dutch
  if (locale === 'en' && !to.path.startsWith('/en')) {
    next(`/en${to.path}`); // Redirect to the English version of the path
  } else if (locale === 'nl' && to.path.startsWith('/en')) {
    next(to.path.replace('/en', '')); // Redirect to Dutch path
  } else {
    next(); // Proceed as normal
  }
});


export default router;
