import { ref, computed } from 'vue';
import en from '@/locales/en.json';
import nl from '@/locales/nl.json';

const messages = { en, nl };
const savedLocale = localStorage.getItem('locale'); // Retrieve saved locale from localStorage
const currentLocale = ref(savedLocale || 'nl'); // Default to 'nl' if nothing is saved

export const i18n = {
    locale: computed(() => currentLocale.value),
    t: (key) => {
        // Handle nested keys
        const localeMessages = messages[currentLocale.value] || {};
        return key.split('.').reduce((obj, k) => obj && obj[k], localeMessages) || key;
    },
    changeLanguage(locale) {
        if (messages[locale]) {
            currentLocale.value = locale;
            localStorage.setItem('locale', locale); // Save the selected locale to localStorage
        }
    }
};
