import en from '@/locales/en.json'
import nl from '@/locales/nl.json'

const messages = { en, nl }
let currentLocale = 'en'

export const i18n = {
  t(key) {
    return messages[currentLocale][key] || key
  },
  changeLanguage(locale) {
    if (messages[locale]) {
      currentLocale = locale
    }
  },
  getCurrentLocale() {
    return currentLocale
  }
}
