import { ref, computed } from 'vue'
import en from '@/locales/en.json'
import nl from '@/locales/nl.json'

const messages = { en, nl }
const currentLocale = ref('nl') // Changed default to 'nl'

export const i18n = {
  locale: computed(() => currentLocale.value),
  t: (key) => computed(() => messages[currentLocale.value][key] || key),
  changeLanguage(locale) {
    if (messages[locale]) {
      currentLocale.value = locale
      if (locale === 'en') {
        if (!window.location.pathname.startsWith('/en')) {
          window.history.pushState({}, '', `/en${window.location.pathname}`)
        }
      } else {
        window.history.pushState({}, '', window.location.pathname.replace(/^\/en/, ''))
      }
    }
  }
}
