import { createWebHistory, createRouter } from "vue-router";
import { i18n } from '../plugins/i18n'

const routes = [
  {
    path: "/:locale?",
    name: "index",
    component: () => import('../pages/index.vue'),
  },
  {
    path: "/:locale?/contact-one",
    name: "contact-one",
    component: () => import('../pages/contact-one.vue'),
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});


// Navigation guard to handle locale and set the correct language
router.beforeEach((to, from, next) => {
  const locale = to.params.locale || '';  // Default to 'nl'
  if (!['en', 'nl'].includes(locale)) {
    return next('nl');  // Redirect to default locale if not valid
  }

  i18n.changeLanguage(locale);  // Change language based on route
  next();
});


export default router;
