import { createWebHistory, createRouter } from "vue-router";
import { i18n } from '../plugins/i18n'

const routes = [
  {
    path: "/:locale?",
    name: "index",
    component: () => import('../pages/index.vue'),
  },
  {
    path: "/:locale?/contact-one",
    name: "contact-one",
    component: () => import('../pages/contact-one.vue'),
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

let isHardRefresh = false;
window.addEventListener('beforeunload', () => {
  isHardRefresh = true;
});

router.beforeEach((to, from, next) => {
  if (isHardRefresh && !sessionStorage.getItem('initialLoad')) {
    sessionStorage.setItem('initialLoad', 'true');
    localStorage.setItem('locale', 'nl');
    i18n.changeLanguage('nl');
    next({ path: '/' });
    return;
  }

  const savedLocale = localStorage.getItem('locale');
  const locale = to.params.locale || savedLocale || 'nl';
  
  if (!['en', 'nl'].includes(locale)) {
    next('nl');
    return;
  }
  
  i18n.changeLanguage(locale);
  
  if (locale === 'en' && !to.path.startsWith('/en')) {
    next(`/en${to.path}`);
    return;
  }
  
  next();
});

export default router;
